import { useState } from "react";
import { v4 } from "uuid";
import s from "./ToDoForm.module.scss";

const radio = {
  OPEN: "open",
  CLOSE: "close",
};

const formatDate = (date) => {
  const day = date.getDay();
  const month = date.getMonth();
  const year = date.getFullYear();
  return `${year}-${month + 1}-${day < 9 ? "0" + day : day}`;
};

const ToDoForm = ({ addToDo }) => {
  const [title, setTitle] = useState("");
  const [descr, setDescr] = useState("");
  const [date, setDate] = useState(formatDate(new Date()));
  const [priority, setPriority] = useState("low");
  const [agree, setAgree] = useState(false);



  const handleChange = (e) => {
    const { name, value, checked } = e.target;
    switch (name) {
      case "title":
        setTitle(value);
        return;
      case "descr":
        setDescr(value);
        return;
      case "date":
        setDate(value);
        return;
      case "priority":
        setPriority(value);
        return;
      case "agree":
        setAgree(checked);
      default:
        return;
    }
  };

  // componentDidUpdate(prevProps, prevState) {
  //   if (prevState.agree !== this.state.agree && this.state.agree === false) {
  //     this.setState({ descr: "CDU" });
  //     alert("отметьте согласие с политикой");
  //   }
  // }

  // shouldComponentUpdate(nextProps, nextState) {
  //   if (nextState !== this.state) {
  //     return true;
  //   } else {
  //     return false;
  //   }
  // }

  const handleSubmit = (e) => {
    e.preventDefault();
    // if (!this.state.agree) return;
    const todo = { title, descr, date, priority, agree, id: v4() };
    addToDo(todo);
  };

  // for (let i = 0; i < 1000000000; i++) {}
  return (
    <form className={s.form} onSubmit={handleSubmit}>
      <label className={s.label}>
        <span> Date </span>
        <input
          className={s.input}
          type="date"
          name="date"
          value={date}
          onChange={handleChange}
        />
      </label>
      <label className={s.label}>
        <span> Title </span>
        <input
          className={s.input}
          type="text"
          name="title"
          value={title}
          onChange={handleChange}
        />
      </label>
      <label className={s.label}>
        <span> Description </span>
        <input
          className={s.input}
          type="text"
          name="descr"
          value={descr}
          onChange={handleChange}
        />
      </label>
      <label>
        <span>Priority</span>
        <select name="priority" value={priority} onChange={handleChange}>
          <option value="low">Low</option>
          <option value="media">Media</option>
          <option value="high">High</option>
        </select>
      </label>
      <label className={s.label}>
        <input
          className={s.input}
          type="checkbox"
          name="agree"
          checked={agree}
          onChange={handleChange}
        />
        <span> Agree with our policy </span>
      </label>
      <button
        disabled={!agree}
        className={s.btn}
        style={{ backgroundColor: !agree && "red" }}
        type="submit"
      >
        Ok
      </button>
    </form>
  );
};

export default ToDoForm;

